mapper.insert:

    SELECT MAX(BOARD_NO) FROM BOARD;

INSERT INTO BOARD (
    NAME
) VALUES (
    #{name}
)

mapper.select:
SELECT COUNT(*) CNT
  FROM POST
 WHERE BOARD_NO = #{boardNo}

mapper.insert:
INSERT INTO POST (
      BOARD_NO
    , USER_ID
    , NICKNAME
    , TITLE
    , CONTENT
    , CREATE_DT
    , UPDATE_DT
) VALUES (
      #{boardNo }
    , #{userId }
    , #{nickname }
    , #{title }
    , #{content }
    , NOW()
    , NOW()
)

mapper.update:
UPDATE POST
   SET TITLE = #{title}
     , CONTENT = #{content}
     , UPDATE_DT = NOW()
 WHERE BOARD_NO = #{boardNo}
   AND POST_NO = #{postNo}

mapper.delete:
DELETE FROM POST
 WHERE BOARD_NO = #{boardNo}

   AND POST_NO = #{postNo}
